import React, { useState } from 'react';
import axios from 'axios';
import { Chess } from 'chess.js';
import Navbar from './components/NavBar';
import UserInput from './components/UserInput';
import TacticDisplay from './components/TacticDisplay';

function App() {
    const [username, setUsername] = useState('e4Guardian');
    const [tactics, setTactics] = useState([]);
    const [shortVariation, setShortVariation] = useState(false);

    const generate = async () => {
        try {
            const response = await axios.get(`https://lichess.org/api/games/user/${username}?max=50&analysed=true&evals=true&moves=true&perfType=blitz,rapid,classical`, {
                headers: { 'Accept': 'application/x-ndjson' }
            });
            const games = response.data.split('\n').filter(x => x).map(x => JSON.parse(x));
            const blunders = findBlunders(games);
            const newTactics = generateTactics(blunders);
            setTactics(newTactics);
        } catch (error) {
            console.error('Error fetching games:', error);
        }
    };

    const findBlunders = (games) => {
        let blunders = [];
        games.forEach(game => {
            game.analysis.forEach((move, index) => {
                if (move.judgment && move.judgment.name === 'Blunder' && index > 0) {
                    const prevMove = game.analysis[index - 1];
                    if (prevMove.judgment && prevMove.judgment.name === 'Blunder') {
                        blunders.push({
                            game: game,
                            index: index - 1,
                            eval: move.eval,
                            variation: move.variation
                        });
                    }
                }
            });
        });
        return blunders;
    };

    const generateTactics = (blunders, shortVariation) => {
        const tactics = [];

        blunders.forEach((blunder, index) => {
            const game = new Chess();
            const moves = blunder.game.moves.split(' ');
            let result = '1-0'; // Default result
            let termination = '0'; // Default termination, could be 'mate' or eval

            // Simulate moves up to the blunder
            for (let i = 0; i <= blunder.index; i++) {
                const move = moves[i];
                if (i === blunder.index) {
                    // Determine game result based on blunder move's player
                    termination = blunder.game.analysis[i].eval || `mate:${blunder.game.analysis[i].mate}`;
                    termination = (typeof termination === "undefined" ? 'mate:'+blunder.game.analysis[i].mate : termination.toString());
                    if ((i + 1) % 2 !== 0) { // The blunder move was made by white, so black wins
                        result = '0-1';
                    }
                    break;
                }
                game.move(move);
            }

            let fen = game.fen();
            let variation = blunder.variation.split(' ');
            variation.unshift(moves[blunder.index]); // Prepend blunder move to the variation

            if (shortVariation && !termination.includes('mate')) {
                variation = variation.slice(0, 6);
            }

            // Setup a new game from the position of the blunder and play the variation
            const newGame = new Chess(fen);
            variation.forEach(move => {
                if (newGame.move(move) === null) {
                    console.error(`Invalid variation move attempted: ${move} in variation ${variation.join(' ')}`);
                }
            });

            // Add headers and finalize the PGN
            addHeaders(newGame, blunder, index, result, termination);
            tactics.push(newGame.pgn());
        });

        return tactics;
    };

    function addHeaders(game, blunder, index, result, termination) {
        game.header('Event', 'Blunder Analysis');
        game.header('Site', 'Internal');
        game.header('Date', new Date().toISOString().slice(0, 10));
        game.header('Round', index + 1);
        game.header('White', blunder.game.white);
        game.header('Black', blunder.game.black);
        game.header('Result', result);
        game.header('Termination', termination);
        game.header('Annotator', 'Blunder Analysis Tool');
    }


    return (
        <div id="app">
            <section className="hero is-fullheight">
                <div className="hero-head">
                    <Navbar />
                </div>
                <div className="hero-body">
                    <div className="container">
                        <UserInput
                            username={username}
                            setUsername={setUsername}
                            generate={generate}
                            shortVariation={shortVariation}
                            setShortVariation={setShortVariation}
                        />
                        <div className="has-text-centered">
                            Enter your Lichess username to generate your tactics based on blunders.
                        </div>
                        <br />
                        <TacticDisplay tactics={tactics} />
                    </div>
                </div>
            </section>
        </div>
    );
}

export default App;
