import React, { useState } from 'react';
import axios from 'axios';
import { Chess } from 'chess.js';
import Navbar from './components/NavBar';
import UserInput from './components/UserInput';
import TacticDisplay from './components/TacticDisplay';

function App() {
    const [username, setUsername] = useState('e4Guardian');
    const [tactics, setTactics] = useState([]);
    const [blunderPositions, setBlunderPositions] = useState([]); // To store FENs
    const [shortVariation, setShortVariation] = useState(false);

    const generate = async () => {
        try {
            const response = await axios.get(`https://lichess.org/api/games/user/${username}?max=50&analysed=true&evals=true&moves=true&pgnInJson=true&perfType=blitz,rapid,classical`, {
                headers: { 'Accept': 'application/x-ndjson' }
            });
          console.log('response: ', response);
            const games = response.data.split('\n').filter(x => x).map(x => JSON.parse(x));
          console.log("games: ", games);
            const blunders = findBlunders(games, username);
            const { newTactics, newPositions } = generateTactics(blunders);
            setTactics(newTactics);
            setBlunderPositions(newPositions); // Update the blunder positions state
        } catch (error) {
            console.error('Error fetching games:', error);
        }
    };

    // const findBlunders = (games) => {
    //     let blunders = [];
    //     games.forEach(game => {
    //         game.analysis.forEach((move, index) => {
    //             if (move.judgment && move.judgment.name === 'Blunder' && index > 0) {
    //                 const prevMove = game.analysis[index - 1];
    //                 if (prevMove.judgment && prevMove.judgment.name === 'Blunder') {
    //                     blunders.push({
    //                         game: game,
    //                         index: index - 1,
    //                         eval: move.eval,
    //                         variation: move.variation
    //                     });
    //                 }
    //             }
    //         });
    //     });
    //     return blunders;
    // };

    function findBlunders(games, username) {
      let blunderFENs = [];

      games.forEach((game) => {
        console.log("game: ", game);
        console.log(game.pgn);

        // Determine which color the user is playing
        const moves = game.moves;
        const players = game.players;
        const userColor = players.white.user.name === username ? 'white' : 'black';
        const analysis = game.analysis;
        const moveList = moves.split(' ');
        console.log(moveList);
        const chess = new Chess();

        let idx = 0;


        // Process each move
        moveList.forEach((move) => {
          // Make the move on the board
          console.log(chess.fen());
          console.log(chess.ascii());
          chess.move(move);

          // Check if the current move is a blunder by the user
          if (analysis[idx] && analysis[idx].judgment && analysis[idx].judgment.name === 'Blunder') {
            const currentPlayer = idx % 2 === 0 ? 'white' : 'black';

            // Check if the blunder was made by the user
            if (currentPlayer === userColor) {

              // Undo the blunder move to get the position before the move
              chess.undo();
              // Add the FEN of the position before the blunder
              blunderFENs.push(chess.fen());
            }
          }
          idx++;
        });
      });

      return blunderFENs;
    }


    const generateTactics = (blunders) => {
        const tactics = [];
        const positions = [];

        blunders.forEach((blunder, index) => {
            const game = new Chess();
            const moves = blunder.game.moves.split(' ');

            // Simulate moves up to just before the blunder
            moves.slice(0, blunder.index).forEach(move => game.move(move));

            const fenBeforeBlunder = game.fen(); // Capture the FEN before the blunder
            positions.push(fenBeforeBlunder); // Save the FEN

            // Continue to simulate to include the blunder move
            game.move(moves[blunder.index]);

            let fen = game.fen();
            let variation = blunder.variation.split(' ');
            variation.unshift(moves[blunder.index]); // Prepend blunder move to the variation

            if (shortVariation && !blunder.eval.includes('mate')) {
                variation = variation.slice(0, 6);
            }

            const newGame = new Chess(fen);
            variation.forEach(move => {
                if (newGame.move(move) === null) {
                    console.error(`Invalid variation move attempted: ${move} in variation ${variation.join(' ')}`);
                }
            });

            addHeaders(newGame, blunder, index, '1-0', blunder.eval);
            tactics.push(newGame.pgn());
        });

        return { newTactics: tactics, newPositions: positions };
    };

    function addHeaders(game, blunder, index, result, termination) {
        game.header('Event', 'Blunder Analysis');
        game.header('Site', 'Internal');
        game.header('Date', new Date().toISOString().slice(0, 10));
        game.header('Round', index + 1);
        game.header('White', blunder.game.white);
        game.header('Black', blunder.game.black);
        game.header('Result', result);
        game.header('Termination', termination);
        game.header('Annotator', 'Blunder Analysis Tool');
    }

    return (
        <div id="app">
            <section className="hero is-fullheight">
                <div className="hero-head">
                    <Navbar />
                </div>
                <div className="hero-body">
                    <div className="container">
                        <UserInput
                            username={username}
                            setUsername={setUsername}
                            generate={generate}
                            shortVariation={shortVariation}
                            setShortVariation={setShortVariation}
                        />
                        <div className="has-text-centered">
                            Enter your Lichess username to generate your tactics based on blunders.
                        </div>
                        <br />
                        <TacticDisplay tactics={tactics} />
                        {/* Optionally display blunder positions */}
                        {blunderPositions.map((fen, index) => (
                            <div key={index}>
                                <p>FEN before blunder: {fen}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </section>
        </div>
    );
}

export default App;
